import flaskfrom web.controller.admin import route_adminfrom common.libs.WebHelper import ops_renderfrom common.model.data import datafrom application import db, appfrom flask import request, jsonifyimport json@route_admin.route('/index', methods=['GET', 'POST'])def admin_index():    resp = {'code': 200, 'totalCount': 0, 'pageTotal': 0, 'list': {}, 'curPage': 1}    req = request.values    page = req['page'] if 'page' in req else 1    f0 = req['f0'] if 'f0' in req else -1    f1 = req['f1'] if 'f1' in req else -1    f2 = req['f2'] if 'f2' in req else -1    offset = int(int(page) - 1) * 100    if f0 != -1:        query = data.query.filter_by(f0=f0)    if f1 != 'null' and f1 != '-1':        query = data.query.filter_by(f1=f1)    if f2 != 'null' and f2 != '-1':        query = data.query.filter_by(f2=f2)    totalCount = query.count()    list = query.offset(offset).limit(100).all()    resp['totalCount'] = totalCount    resp['list'] = list    resp['pageTotal'] = (totalCount // 100)    resp['curPage'] = page    resp['f0'] = f0    resp['f1'] = f1    resp['f2'] = f2    return ops_render('/admin/apply.html', resp)@route_admin.route('/delete', methods=['GET', 'POST'])def delete():    resp = {'code': 200, 'totalCount': 0, 'pageTotal': 0, 'list': {}, 'curPage': 1}    req = request.values    id = req['id']    article1 = data.query.filter_by(id=id).first()    db.session.delete(article1)    db.session.commit()    resp['msg'] = '操作成功'    return jsonify(resp)@route_admin.route('/show_content', methods=['GET', 'POST'])def show_content():    resp = {'code': 200, 'totalCount': 0, 'pageTotal': 0, 'list': {}, 'curPage': 1}    req = request.values    id = req['id']    article1 = data.query.filter_by(id=id)    resp['msg'] = article1[0].content    return jsonify(resp)@route_admin.route('/getinfo/', methods=['GET', 'POST'])def post():    '''    f0：    f1:    f2:    :return:    '''    response = {'code': 200, 'msg': '查询成功'}    req = request.values    f0 = req['f0'] if 'f0' in req else -1    f1 = req['f1'] if 'f1' in req else -1    f2 = req['f2'] if 'f2' in req else -1    if f0 != -1:        query = data.query.filter_by(f0=f0)    if f1 != '-1':        query = data.query.filter_by(f1=f1)    if f2 != '-1':        query = data.query.filter_by(f2=f2)    result = query.all()    tmp_list = []    for item in result:        tmp_data = {            'id': item.id,            'merchant_id': item.title,            'time': item.time,            'image': item.image,            'Introduction': item.Introduction        }        tmp_list.append(tmp_data)    res = flask.make_response()    res.headers['Access-Control-Allow-Origin'] = '*'    res.headers['Access-Control-Allow-Methods'] = 'POST，GET,OPTIONS'    res.headers['Access-Control-Allow-Headers'] = 'x-requested-with,content-type'    response['list'] = tmp_list    resp = jsonify(response)    resp.headers['Access-Control-Allow-Origin'] = '*'    return resp@route_admin.route('/getinfo/getcontent/', methods=['GET', 'POST'])def post_content():    response = {'code': 200, 'msg': '查询成功'}    req = request.get_json    id = req['id'] if 'f0' in req else -1    # print(id)    if id != '-1':        query = data.query.filter_by(id=id)    result = query.all()    tmp_list = []    for item in result:        tmp_data = {            'id': item.id,            'content': item.content,            'content_url': item.content_url,            'content_image': item.content_image,        }        tmp_list.append(tmp_data)    response['result'] = tmp_list    resp = jsonify(response)    resp.headers['Access-Control-Allow-Origin'] = '*'    return resp